  const [cart, setCart] = React.useState([]);

  // add to cart 
  const addToCart = () => {
    console.log('added to the cart')
  }

  return (
  <CartContext.Provider value={'this is the cart context'}>
    {children}
    </CartContext.Provider>
    );
};

export default CartProvider;
