import React, {useContext} from 'react';
// import params 
import {useParams} from 'react-router-dom';
// import cart context 
import {CartContext} from '../contexts/CartContext';
// import product context 
import {ProductContext} from '../contexts/ProductContext';

const ProductDetails = () => {
  // get the product id from the url 
  const { id } = useParams();
  const { products } = useContext(ProductContext);
  const { addToCart } = useContext(CartContext);

  // get the single product based on the id 
  const product = products.find((item) => {
    // parseInt is used to retrieve the product details from the api using an id 
    return item.id === parseInt(id);
  })
  // console.log(product)
  // if product is not found
  if (!product) { 
    return <section className='h-screen flex justify-center items-center'>Loading...</section>
  }

  // destructure products 
  const { title, price, description, image} = product;
  return <section className='pt-32 pb-12 lg:py-32 h-screen flex items-center'>
    <div className="container mx-auto">
      {/* image and text wrapper */}
      <div className='flex flex-col lg:flex-row items-center'>
      {/* image */}
      <div className='flex flex-1 justify-center items-center mb-8 lg:mb-0'>
        <img className='max-w-[200px] lg:max-w-sm' src={image} alt="" />
      </div>
      {/* text */}
      <div className='flex-1 text-center lg:text-left'>
        <h1 className="text-[26px] font-medium mb-2 max-w-[450px] mx-auto">
          {title}
        </h1>
        <div className='text-xl text-red-500 font-medium mb-6'>
          ${price}
        </div>
      </div>
      </div>
    </div>
  </section>;
};

export default ProductDetails;
